import React, { useState } from 'react';
import Modal from './Modal';
import LabWork1 from '../pages/LabWork1';
import LabWork2 from '../pages/LabWork2';
import LabWork3 from '../pages/LabWork3';
import LabWork4 from '../pages/LabWork4';
import LabWork5 from '../pages/LabWork5';

const LabWorks = () => {
  const [modalOpen, setModalOpen] = useState(null);

  const openModal = (work) => {
    setModalOpen(work);
  };

  const closeModal = () => {
    setModalOpen(null);
  };

  return (
    <section id="lab-works">
      <h2>Лабораторні роботи</h2>
      <ul>
        <li><button onClick={() => openModal('lab1')}>Лабораторна робота 1</button></li>
        <li><button onClick={() => openModal('lab2')}>Лабораторна робота 2</button></li>
        <li><button onClick={() => openModal('lab3')}>Лабораторна робота 3</button></li>
        <li><button onClick={() => openModal('lab4')}>Лабораторна робота 4</button></li>
        <li><button onClick={() => openModal('lab5')}>Лабораторна робота 5</button></li>
      </ul>

      {modalOpen === 'lab1' && <Modal content={<LabWork1 />} onClose={closeModal} />}
      {modalOpen === 'lab2' && <Modal content={<LabWork2 />} onClose={closeModal} />}
      {modalOpen === 'lab3' && <Modal content={<LabWork3 />} onClose={closeModal} />}
      {modalOpen === 'lab4' && <Modal content={<LabWork4 />} onClose={closeModal} />}
      {modalOpen === 'lab5' && <Modal content={<LabWork5 />} onClose={closeModal} />}
    </section>
  );
};

export default LabWorks;
