import React, { useState, useRef, useEffect } from 'react';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css'; // Подключаем файл стилей

const RegistrationForm = ({ onClose }) => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    console.log(formData);
  };

  return (
    <div className="modal-content">
      <button className="btn btn-secondary close-btn" onClick={onClose}>Close</button>
      <form className="p-5 registration-form" onSubmit={handleSubmit}>
        <h2 className="text-center">Registraton Form</h2>
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            className="form-control"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleChange}
            placeholder="Enter your username"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            className="form-control"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter your email"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            className="form-control"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Enter your password"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="confirmPassword">Confirm Password</label>
          <input
            type="password"
            className="form-control"
            id="confirmPassword"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            placeholder="Confirm your password"
            required
          />
        </div>
        <button type="submit" className="btn btn-primary btn-block mt-4">Register</button>
      </form>
    </div>
  );
};

function App() {
  const [showModal, setShowModal] = useState(false);
  const canvasRef = useRef(null);

  const handleShow = () => setShowModal(true);
  const handleClose = () => setShowModal(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      const width = canvas.width;
      const height = canvas.height;

      ctx.clearRect(0, 0, width, height);

      // Draw the arrow
      ctx.save();
      ctx.translate(width / 2, height / 2); // Move to center

      // Define arrow points
      const size = 20;
      const tailLength = 60; // Tail length
      const arrowWidth = 15;

      // Draw the arrow
      ctx.beginPath();
      ctx.moveTo(0, 0);
      ctx.lineTo(size, -size);
      ctx.lineTo(size + arrowWidth, -size);
      ctx.lineTo(size + arrowWidth, -size / 2);
      ctx.lineTo(tailLength, -size / 2);
      ctx.lineTo(tailLength, size / 2);
      ctx.lineTo(size + arrowWidth, size / 2);
      ctx.lineTo(size + arrowWidth, size);
      ctx.lineTo(size, size);
      ctx.closePath();

      
      const gradient = ctx.createLinearGradient(-width / 2, -height / 2, width / 2, height / 2);
      gradient.addColorStop(0, 'red');
      gradient.addColorStop(1, 'black');
      ctx.fillStyle = gradient;
      ctx.fill();

  
      ctx.lineWidth = 3;
      ctx.strokeStyle = 'white';
      ctx.stroke();
      
      ctx.restore();
    }
  }, []);

  return (
    <div className="container">
      <h1 className="text-center mt-5">Lab1 Registration Form - Click on button</h1>
      <button className="btn btn-primary position-relative" onClick={handleShow}>
        Register
        <canvas
          ref={canvasRef}
          width={150} 
          height={150} 
          className="arrow-canvas"
        />
      </button>

      {showModal && (
        <div className="modal-overlay">
          <RegistrationForm onClose={handleClose} />
        </div>
      )}
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById('root'));
