import React from 'react';
import '../styles/LabStyle/PracticalWork1.css';

// Генерация рабочих дней с датами
const generateDates = (startDate, days) => {
  const dates = [];
  for (let i = 0; i < days; i++) {
    const date = new Date(startDate);
    date.setDate(startDate.getDate() + i);
    const dayOfWeek = date.getDay();
    // Только рабочие дни (Пн-Пт)
    if (dayOfWeek >= 1 && dayOfWeek <= 5) {
      dates.push(`${date.getDate().toString().padStart(2, '0')}.09.2024`);
    }
  }
  return dates;
};

// Генерация расписания
const generateSchedule = () => {
  const subjects = ['ВМ', 'КДС', 'Python', 'C++', 'C#'];
  const types = ['[Лб]', '[Пз]', '[Лк]'];
  const timeSlots = [
    '08:00 - 09:30',
    '09:40 - 11:10',
    '11:20 - 12:50',
    '13:00 - 14:30',
    '14:40 - 16:10',
    '16:20 - 17:50'
  ];
  const schedule = [];
  const totalDays = 30; // Сентябрь имеет 30 дней
  const dates = generateDates(new Date(2024, 8, 1), totalDays); // Начинается с 1 сентября

  for (let i = 0; i < dates.length; i += 7) {
    const weekDates = dates.slice(i, i + 7);
    const weekSchedule = weekDates.map(date => {
      const numLessons = Math.floor(Math.random() * 4) + 3; // 3 до 4 занятий в день
      const lessons = [];
      
      let startSlot = Math.floor(Math.random() * 3); // 0 для 1-й пары, 1 для 2-й, 2 для 3-й пары

      for (let j = 0; j < numLessons; j++) {
        const subject = subjects[Math.floor(Math.random() * subjects.length)];
        const type = types[Math.floor(Math.random() * types.length)];
        lessons[startSlot + j] = `${subject} ${type}`;
      }

      return {
        date,
        lessons: lessons,
      };
    });
    
    schedule.push(weekSchedule);
  }
  
  return schedule;
};

const scheduleData = generateSchedule();

const getLessonClass = (type) => {
  if (type.includes('[Лб]')) return 'lab'; // Лабораторные работы
  if (type.includes('[Пз]')) return 'practice'; // Практические занятия
  if (type.includes('[Лк]')) return 'lecture'; // Лекции
  return '';
};

const PracticalWork1 = () => {
  return (
    <div className="schedule-container">
      <h3>Розклад занять на вересень</h3>
      <div className="schedule-scroll-container">
        {scheduleData.map((week, weekIndex) => (
          <div className="week-block" key={weekIndex}>
            <table className="schedule-table">
              <thead>
                <tr>
                  <th>Час/Дата</th>
                  {week.map((day, index) => (
                    <th key={index} className="date-header">{day.date}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {week[0].lessons.map((_, lessonIndex) => (
                  <tr key={lessonIndex}>
                    <td>{week[0].lessons[lessonIndex]}</td>
                    {week.map((day, dayIndex) => (
                      <td key={dayIndex} className={getLessonClass(day.lessons[lessonIndex])}>
                        {day.lessons[lessonIndex]}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PracticalWork1;
