import React from 'react';
import '../styles/LabStyle/PracticalWork1.css';

const daysInMonth = 30; // September has 30 days

const generateSchedule = () => {
  const subjects = ['ВМ', 'КДС', 'Python', 'C++', 'C#'];
  const types = ['[Лб]', '[Пз]', '[Лк]'];
  const schedule = [];
  
  for (let day = 1; day <= daysInMonth; day++) {
    const numLessons = Math.floor(Math.random() * 3) + 3; // 3 to 4 lessons per day
    const lessons = [];
    
    let startSlot = Math.floor(Math.random() * 3); // 0 for 1st pair, 1 for 2nd, 2 for 3rd pair

    for (let i = 0; i < numLessons; i++) {
      const subject = subjects[Math.floor(Math.random() * subjects.length)];
      const type = types[Math.floor(Math.random() * types.length)];
      lessons[startSlot + i] = `${subject} ${type}`;
    }
    
    schedule.push({
      date: `01/${day.toString().padStart(2, '0')}`, // Format date as dd/mm
      time: getTimeSlot(startSlot),
      lessons: [
        lessons[0] || '',
        lessons[1] || '',
        lessons[2] || '',
        lessons[3] || '',
        lessons[4] || '',
        lessons[5] || '',
      ],
    });
  }
  
  return schedule;
};

const getTimeSlot = (startSlot) => {
  switch (startSlot) {
    case 0: return '08:00 - 09:30';
    case 1: return '09:40 - 11:10';
    case 2: return '11:20 - 12:50';
    default: return '08:00 - 09:30';
  }
};

const scheduleData = generateSchedule();

const getLessonClass = (type) => {
  if (type.includes('[Лб]')) return 'lab'; // Lab
  if (type.includes('[Пз]')) return 'practice'; // Practice
  if (type.includes('[Лк]')) return 'lecture'; // Lecture
  return '';
};

const PracticalWork1 = () => {
  return (
    <div className="schedule-container">
      <h3>Розклад занять на вересень</h3>
      <div className="schedule-scroll-container">
        <table className="schedule-table">
          <thead>
            <tr>
              <th>Час/Дата</th>
              {[...Array(6)].map((_, idx) => (
                <th key={idx}>День {idx + 1}</th>
              ))}
            </tr>
            <tr>
              <th></th>
              {scheduleData.slice(0, 6).map((day, index) => (
                <th key={index} className="date-header">{day.date}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {scheduleData.map((slot, index) => (
              <tr key={index}>
                <td>{slot.time}</td>
                {slot.lessons.map((lesson, idx) => (
                  <td key={idx} className={getLessonClass(lesson)}>
                    {lesson}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PracticalWork1;
