import React from 'react';
import '../styles/LabStyle/PracticalWork1.css'; 

const scheduleData = [
  { time: '08:00 - 09:30', lessons: ['Лб', 'Лк', 'Пз', 'Лб', 'Лк', 'Пз', 'Лб'] },
  { time: '09:40 - 11:10', lessons: ['Лк', 'Пз', 'Лб', 'Лк', 'Пз', 'Лб', 'Лк'] },
  { time: '11:20 - 12:50', lessons: ['Пз', 'Лб', 'Лк', 'Пз', 'Лб', 'Лк', 'Пз'] },
  { time: '13:00 - 14:30', lessons: ['Лб', 'Лк', 'Пз', 'Лб', 'Лк', 'Пз', 'Лб'] },
  { time: '14:40 - 16:10', lessons: ['Лк', 'Пз', 'Лб', 'Лк', 'Пз', 'Лб', 'Лк'] },
  { time: '16:20 - 17:50', lessons: ['Пз', 'Лб', 'Лк', 'Пз', 'Лб', 'Лк', 'Пз'] },
];

const getLessonClass = (type) => {
  switch(type) {
    case 'Лб': return 'lab';
    case 'Пз': return 'practice';
    case 'Лк': return 'lecture';
    default: return '';
  }
};

const PracticalWork1 = () => {
  return (
    <div className="schedule-container">
      <h3>Розклад занять</h3>
      <table className="schedule-table">
        <thead>
          <tr>
            <th>Час/Дата</th>
            {['01.10', '02.10', '03.10', '04.10', '05.10', '06.10', '07.10'].map(date => (
              <th key={date}>{date}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {scheduleData.map(({ time, lessons }) => (
            <tr key={time}>
              <td>{time}</td>
              {lessons.map((lesson, index) => (
                <td key={index} className={getLessonClass(lesson)}>{lesson}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PracticalWork1;
