import React, { useState, useRef, useEffect } from 'react';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css'; // Подключаем файл стилей

function App() {
  const [showModal, setShowModal] = useState(false);
  const canvasRef = useRef(null);

  const handleShow = () => setShowModal(true);
  const handleClose = () => setShowModal(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      const width = canvas.width;
      const height = canvas.height;

      ctx.clearRect(0, 0, width, height);

      // Draw the arrow
      ctx.save();
      ctx.translate(width / 2, height / 2); // Move to center
      ctx.rotate(Math.PI); // Rotate 180 degrees

      // Define arrow points
      const size = 20;
      const tailLength = 60; // Tail length

      // Draw the arrow
      ctx.beginPath();
      ctx.moveTo(0, 0);
      ctx.lineTo(-size, -size);
      ctx.lineTo(-size / 2, -size / 2);
      ctx.lineTo(-tailLength, -size / 2);
      ctx.lineTo(-tailLength, size / 2);
      ctx.lineTo(-size / 2, size / 2);
      ctx.lineTo(-size, size);
      ctx.closePath();

      // Create a gradient
      const gradient = ctx.createLinearGradient(-width / 2, -height / 2, width / 2, height / 2);
      gradient.addColorStop(0, 'red');
      gradient.addColorStop(1, 'black');
      ctx.fillStyle = gradient;
      ctx.fill();

      // Draw arrow outline
      ctx.lineWidth = 3;
      ctx.strokeStyle = 'white';
      ctx.stroke();
      
      ctx.restore();
    }
  }, []);

  return (
    <div className="container">
      <h1 className="text-center mt-5">Welcome</h1>
      <button className="btn btn-primary position-relative" onClick={handleShow}>
        Register
        <canvas
          ref={canvasRef}
          width={150} // Adjust based on desired size
          height={150} // Adjust based on desired size
          className="arrow-canvas"
          style={{ position: 'absolute', right: '-120px', top: '50%', transform: 'translateY(-50%)' }}
        />
      </button>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="btn-close" onClick={handleClose}>×</button>
            <h2 className="text-center">Registration Form</h2>
            <form>
              <div className="mb-3">
                <label htmlFor="username" className="form-label">Username</label>
                <input type="text" className="form-control" id="username" placeholder="Enter username" />
              </div>
              <div className="mb-3">
                <label htmlFor="email" className="form-label">Email</label>
                <input type="email" className="form-control" id="email" placeholder="Enter email" />
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">Password</label>
                <input type="password" className="form-control" id="password" placeholder="Enter password" />
              </div>
              <button type="submit" className="btn btn-primary">Register</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById('root'));
