import React, { useState } from 'react';
import Modal from './Modal';
import PracticalWork1 from '../pages/PracticalWork1';
import PracticalWork2 from '../pages/PracticalWork2';
import PracticalWork3 from '../pages/PracticalWork3';
import PracticalWork4 from '../pages/PracticalWork4';
import PracticalWork5 from '../pages/PracticalWork5';

const PracticalWorks = () => {
  const [modalOpen, setModalOpen] = useState(null);

  const openModal = (work) => {
    setModalOpen(work);
  };

  const closeModal = () => {
    setModalOpen(null);
  };

  return (
    <section id="practical-works">
      <h2>Практичні роботи</h2>
      <ul>
        <li><button onClick={() => openModal('practical1')}>Практична робота 1</button></li>
        <li><button onClick={() => openModal('practical2')}>Практична робота 2</button></li>
        <li><button onClick={() => openModal('practical3')}>Практична робота 3</button></li>
        <li><button onClick={() => openModal('practical4')}>Практична робота 4</button></li>
        <li><button onClick={() => openModal('practical5')}>Практична робота 5</button></li>
      </ul>

      {modalOpen === 'practical1' && <Modal content={<PracticalWork1 />} onClose={closeModal} />}
      {modalOpen === 'practical2' && <Modal content={<PracticalWork2 />} onClose={closeModal} />}
      {modalOpen === 'practical3' && <Modal content={<PracticalWork3 />} onClose={closeModal} />}
      {modalOpen === 'practical4' && <Modal content={<PracticalWork4 />} onClose={closeModal} />}
      {modalOpen === 'practical5' && <Modal content={<PracticalWork5 />} onClose={closeModal} />}
    </section>
  );
};

export default PracticalWorks;
