import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';

const PracticalWorkModal = ({ workContent, onClose }) => (
  <div className="modal-overlay">
    <div className="modal-content">
      <button className="btn btn-secondary close-btn" onClick={onClose}>Close</button>
      <div className="work-content">
        {workContent}
      </div>
    </div>
  </div>
);

const App = () => {
  const [showModal, setShowModal] = useState(false);
  const [modalContent, setModalContent] = useState('');

  const handleShow = (content) => {
    setModalContent(content);
    setShowModal(true);
  };

  const handleClose = () => setShowModal(false);

  const practicalWorks = [
    { id: 1, title: 'Практична робота 1', content: 'Тут буде код практичної роботи 1.' },
    { id: 2, title: 'Практична робота 2', content: 'Тут буде код практичної роботи 2.' },
    { id: 3, title: 'Практична робота 3', content: 'Тут буде код практичної роботи 3.' },
    { id: 4, title: 'Практична робота 4', content: 'Тут буде код практичної роботи 4.' },
    { id: 5, title: 'Практична робота 5', content: 'Тут буде код практичної роботи 5.' },
  ];

  return (
    <div className="root">
      <header>
        <nav>
          <ul>
            <li>Головна</li>
            <li>Про мене</li>
            <li>Практичні роботи</li>
            <li>Лабораторні роботи</li>
          </ul>
        </nav>
      </header>

      <section id="about-me">
        <h2>Про мене</h2>
        <p>Тут буде текст про тебе.</p>
      </section>

      <section id="practical-works">
        <h2>Практичні роботи</h2>
        <ul>
          {practicalWorks.map((work) => (
            <li key={work.id}>
              <button className="btn btn-primary" onClick={() => handleShow(work.content)}>
                {work.title}
              </button>
            </li>
          ))}
        </ul>
      </section>

      {showModal && <PracticalWorkModal workContent={modalContent} onClose={handleClose} />}
    </div>
  );
};

export default App;
